import { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import grapesjs from "grapesjs";
import "grapesjs/dist/css/grapes.min.css";
import "grapesjs-preset-webpage";
import "grapesjs-plugin-ckeditor";

function WebpageEditor() {
  const location = useLocation();
  const navigate = useNavigate();
  const userId = new URLSearchParams(location.search).get("user_id");
  const [editor, setEditor] = useState(null);
  const [blogContent, setBlogContent] = useState(null);
  const [contentLoaded, setContentLoaded] = useState(false);

  // **Fetch Blog Content from Database**
  useEffect(() => {
    if (userId) {
      axios.get(`http://localhost:5000/get_blog?user_id=${userId}`)
        .then(response => {
          setBlogContent(response.data);
          console.log("Fetched Blog Content:", response.data);
        })
        .catch(error => console.error("Error fetching blog:", error));
    }
  }, [userId]);

  // **Initialize GrapesJS AFTER Blog Content is Loaded**
  useEffect(() => {
    if (!blogContent || contentLoaded) return;

    const editorInstance = grapesjs.init({
      container: "#gjs",
      height: "100vh",
      width: "100%",
      storageManager: false,
      plugins: ["gjs-preset-webpage", "grapesjs-plugin-ckeditor"],
      pluginsOpts: {
        "gjs-preset-webpage": { navbar: false },
        "grapesjs-plugin-ckeditor": { options: { language: "en" } },
      },
    });

    setEditor(editorInstance);

    // **Insert Fetched Blog Content into GrapesJS**
    if (blogContent.TITLE || blogContent.INTRODUCTION || blogContent.BODY) {
      const blogHtml = `
        <div>
          <h1>${blogContent.TITLE || "Your Blog Title"}</h1>
          <p>${blogContent.INTRODUCTION || "Your introduction text goes here..."}</p>
          <div>${blogContent.BODY || "Your blog body content will be displayed here."}</div>
        </div>
      `;

      editorInstance.setComponents(blogHtml);
      setContentLoaded(true);
    }

  }, [blogContent]);

  // **Save & Navigate to Webpage View**
  const goToWebpageView = () => {
    if (!editor) return;

    const htmlContent = editor.getHtml();
    const cssContent = editor.getCss();

    localStorage.setItem("savedHtml", htmlContent);
    localStorage.setItem("savedCss", cssContent);

    navigate(`/webpage-view?user_id=${userId}`);
  };

  return (
    <div>
      <h2>Webpage Editor</h2>

      {/* GrapesJS Editor */}
      <div id="gjs"></div>

      {/* Navigation Buttons */}
      <div style={{ display: "flex", justifyContent: "space-between", marginTop: "20px" }}>
        <button onClick={() => navigate("/")}>Previous</button>
        <button onClick={goToWebpageView}>Next</button>
      </div>
    </div>
  );
}

export default WebpageEditor;
