import { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
import grapesjs from "grapesjs";
import "grapesjs/dist/css/grapes.min.css";
import "grapesjs-preset-webpage";

function WebpageEditor() {
  const location = useLocation();
  const userId = new URLSearchParams(location.search).get("user_id");
  const [content, setContent] = useState({ title: "", intro: "", body: "" });
  const [editor, setEditor] = useState(null);
  const [useAITemplate, setUseAITemplate] = useState(false);
  const [additionalPrompt, setAdditionalPrompt] = useState("");
  const [imagePrompt, setImagePrompt] = useState(""); // New input for image generation
  const [generatedImage, setGeneratedImage] = useState(null); // Store generated image

  useEffect(() => {
    if (userId) {
      axios.get(`http://localhost:5000/get_blog?user_id=${userId}`)
        .then(response => setContent(response.data))
        .catch(error => console.error("Error fetching blog:", error));
    }
  }, [userId]);

//   useEffect(() => {
//     const editorInstance = grapesjs.init({
//       container: "#gjs",
//       height: "100vh",
//       width: "100%",
//       storageManager: false,
//       plugins: ["gjs-preset-webpage"],
//     });

//     setEditor(editorInstance);
//   }, []);
  useEffect(() => {
    const editorInstance = grapesjs.init({
      container: "#gjs",
      height: "100vh",
      width: "100%",
      storageManager: false,
      plugins: ["gjs-preset-webpage"],
      pluginsOpts: {
        "gjs-preset-webpage": {
          modalImportTitle: "Import Content",
          modalImportButton: "Import",
          navbar: false,
          modalLabelImport: "Paste your HTML/CSS here",
        },
      },
      panels: {
        defaults: [
          {
            id: "layers",
            el: ".panel__right",
            resizable: {
              maxDim: 350,
              minDim: 200,
              tc: 0, // Top resize disabled
              cl: 1, // Left resize enabled
              cr: 0, // Right resize disabled
              bc: 1, // Bottom resize enabled
            },
          },
        ],
      },
    });
  
    // Enable Drag & Drop
    editorInstance.BlockManager.getAll().forEach((block) => {
      block.set({ draggable: true });
    });
  
    // Enable Resizing
    editorInstance.on("component:add", (component) => {
      component.set({
        resizable: {
          tl: 1, // Top-left
          tr: 1, // Top-right
          bl: 1, // Bottom-left
          br: 1, // Bottom-right
          minWidth: 100,
          minHeight: 50,
          maxWidth: "100%",
          maxHeight: "auto",
        },
      });
    });
  
    setEditor(editorInstance);
  }, []);
  

  // Function to load AI-generated HTML template with blog content
  const loadAITemplate = async () => {
    if (!userId) {
      alert("User ID is required!");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/generate_template", {
        user_id: userId,
        additional_prompt: additionalPrompt,
      });

      if (response.data.html) {
        editor.setComponents(response.data.html);
        setUseAITemplate(true);
      }
    } catch (error) {
      console.error("Error fetching AI template:", error);
    }
  };

  // Function to load blog content into the editor
  const loadBlogContent = () => {
    if (editor && content.title) {
      editor.setComponents(`
        <div>
          <h1>${content.title}</h1>
          <p>${content.intro}</p>
          <div>${content.body}</div>
        </div>
      `);
      setUseAITemplate(false);
    }
  };

  // Function to generate an image
  const generateImage = async () => {
    if (!imagePrompt) {
      alert("Please enter an image prompt!");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/generate_image", { prompt: imagePrompt });

      if (response.data.image_url) {
        setGeneratedImage(response.data.image_url);
      }
    } catch (error) {
      console.error("Error generating image:", error);
    }
  };

  // Function to add image to GrapesJS
  const addImageToEditor = () => {
    if (editor && generatedImage) {
      editor.addComponents(`<img src="${generatedImage}" alt="AI Generated Image" style="width:100%;"/>`);
    }
  };

  return (
    <div>
      <h2>Webpage Editor</h2>

      <div style={{ display: "flex", gap: "10px", marginBottom: "10px" }}>
        <button onClick={loadBlogContent} style={{ padding: "10px", flex: 1 }}>
          Load Blog Content
        </button>
        <button onClick={() => setUseAITemplate(true)} style={{ padding: "10px", flex: 1 }}>
          Use AI-Generated Template
        </button>
      </div>

      {/* Show additional prompt input when AI-generated template is selected */}
      {useAITemplate && (
        <div style={{ marginBottom: "10px" }}>
          <textarea
            value={additionalPrompt}
            onChange={(e) => setAdditionalPrompt(e.target.value)}
            placeholder="Enter additional modifications for AI-generated template..."
            style={{ width: "100%", height: "60px", padding: "10px" }}
          />
          <button onClick={loadAITemplate} style={{ padding: "10px", width: "100%", marginTop: "10px" }}>
            Generate AI Template
          </button>
        </div>
      )}

      {/* Image Generation Section */}
      <div style={{ marginBottom: "10px" }}>
        <h3>Generate AI Image</h3>
        <input
          type="text"
          value={imagePrompt}
          onChange={(e) => setImagePrompt(e.target.value)}
          placeholder="Enter image prompt..."
          style={{ width: "100%", padding: "10px" }}
        />
        <button onClick={generateImage} style={{ padding: "10px", width: "100%", marginTop: "10px" }}>
          Generate Image
        </button>
      </div>

      {/* Show generated image with drag-and-drop */}
      {generatedImage && (
        <div>
          <h4>Generated Image:</h4>
          <img src={generatedImage} alt="AI Generated" style={{ maxWidth: "100%", marginBottom: "10px" }} />
          <button onClick={addImageToEditor} style={{ padding: "10px", width: "100%" }}>
            Add Image to Editor
          </button>
        </div>
      )}

      <div id="gjs"></div>
    </div>
  );
}

export default WebpageEditor;



