import { useState, useEffect } from "react";
import axios from "axios";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css"; // Import styles for the editor

function BlogGenerator() {
  const [topic, setTopic] = useState("");
  const [section, setSection] = useState("TITLE");
  const [additionalPrompt, setAdditionalPrompt] = useState("");
  const [content, setContent] = useState({
    title: "",
    intro: "",
    body: "",
  });
  const [editorContent, setEditorContent] = useState(""); // Editor content

  // Update editor content when the section changes
  useEffect(() => {
    if (section === "TITLE") setEditorContent(content.title);
    else if (section === "INTRODUCTION") setEditorContent(content.introduction);
    else if (section === "BODY") setEditorContent(content.body);
  }, [section, content]);

  // Function to handle content generation
  const handleGenerate = async () => {
    const response = await axios.post("http://localhost:5000/generate", {
      topic,
      section,
      additionalPrompt,
    });

    // Update only the generated section
    setContent((prevContent) => ({
      ...prevContent,
      [section.toLowerCase()]: response.data[section.toLowerCase()] || "",
    }));
  };

  // Function to save edited content
  const handleSave = async () => {
    const updatedContent = { ...content, [section.toLowerCase()]: editorContent };
    setContent(updatedContent); // Update UI state

    await axios.post("http://localhost:5000/save", {
      topic,
      content: updatedContent,
    });

    alert("Blog saved successfully!");
  };

  return (
    <div style={{ maxWidth: "800px", margin: "auto", padding: "20px" }}>
      <h2>AI Blog Generator</h2>

      {/* Blog Topic Input */}
      <input
        type="text"
        value={topic}
        onChange={(e) => setTopic(e.target.value)}
        placeholder="Enter blog topic"
        style={{ width: "100%", padding: "10px", marginBottom: "10px" }}
      />

      {/* Section Selector */}
      <select
        value={section}
        onChange={(e) => setSection(e.target.value)}
        style={{ width: "100%", padding: "10px", marginBottom: "10px" }}
      >
        <option value="TITLE">Title</option>
        <option value="INTRODUCTION">Introduction</option>
        <option value="BODY">Body</option>
      </select>

      {/* Additional Prompt Input */}
      <textarea
        value={additionalPrompt}
        onChange={(e) => setAdditionalPrompt(e.target.value)}
        placeholder="Add additional prompt (optional)"
        style={{ width: "100%", padding: "10px", marginBottom: "10px", height: "50px" }}
      />

      <button onClick={handleGenerate} style={{ padding: "10px", width: "100%" }}>
        Generate Content
      </button>

      {/* Single Rich Text Editor */}
      <h3>Editing: {section}</h3>
      <ReactQuill
        theme="snow"
        value={editorContent}
        onChange={setEditorContent} // Updates editor content only
      />

      <button onClick={handleSave} style={{ padding: "10px", width: "100%", marginTop: "20px" }}>
        Save Blog
      </button>
    </div>
  );
}

export default BlogGenerator;



