import { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import grapesjs from "grapesjs";
import "grapesjs/dist/css/grapes.min.css";
import "grapesjs-preset-webpage";
import "grapesjs-plugin-ckeditor";

function WebpageEditor() {
  const location = useLocation();
  const navigate = useNavigate();
  const userId = new URLSearchParams(location.search).get("user_id");
  const [editor, setEditor] = useState(null);
  const [blogContent, setBlogContent] = useState(null);
  const [useAITemplate, setUseAITemplate] = useState(false);
  const [additionalPrompt, setAdditionalPrompt] = useState("");
  const [useAIImage, setUseAIImage] = useState(false);
  const [imagePrompt, setImagePrompt] = useState("");
  const [generatedImage, setGeneratedImage] = useState(null);

  // **Fetch Blog Content Automatically**
  useEffect(() => {
    if (userId) {
      axios.get(`http://localhost:5000/get_blog?user_id=${userId}`)
        .then(response => {
          setBlogContent(response.data);
          console.log("Loaded Blog Content:", response.data);
        })
        .catch(error => console.error("Error fetching blog:", error));
    }
  }, [userId]);

  // **Initialize GrapesJS & Auto-Insert Blog Content**
  useEffect(() => {
    const editorInstance = grapesjs.init({
      container: "#gjs",
      height: "100vh",
      width: "100%",
      storageManager: false,
      plugins: ["gjs-preset-webpage", "grapesjs-plugin-ckeditor"],
      pluginsOpts: {
        "gjs-preset-webpage": { navbar: false },
        "grapesjs-plugin-ckeditor": { options: { language: "en" } },
      },
    });

    setEditor(editorInstance);
  }, []);

  // **When Blog Content is Loaded, Insert into GrapesJS**
  useEffect(() => {
    if (editor && blogContent) {
      const { TITLE, INTRODUCTION, BODY } = blogContent;

      const blogHtml = `
        <div>
          <h1>${TITLE || "Your Blog Title"}</h1>
          <p>${INTRODUCTION || "Your introduction text goes here..."}</p>
          <div>${BODY || "Your blog body content will be displayed here."}</div>
        </div>
      `;

      editor.setComponents(blogHtml);
    }
  }, [editor, blogContent]);

  // **Generate AI Image**
  const generateImage = async () => {
    if (!imagePrompt) {
      alert("Please enter an image prompt!");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/generate_image", { prompt: imagePrompt });

      if (response.data.image_url) {
        setGeneratedImage(response.data.image_url);
      }
    } catch (error) {
      console.error("Error generating image:", error);
    }
  };

  // **Add AI-Generated Image to Editor**
  const addImageToEditor = () => {
    if (editor && generatedImage) {
      editor.addComponents(`<img src="${generatedImage}" alt="AI Generated Image" style="width:100%;"/>`);
    }
  };

  // **Save & Navigate to Webpage View**
  const goToWebpageView = () => {
    const htmlContent = editor.getHtml();
    const cssContent = editor.getCss();

    localStorage.setItem("savedHtml", htmlContent);
    localStorage.setItem("savedCss", cssContent);

    navigate(`/webpage-view?user_id=${userId}`);
  };

  return (
    <div>
      <h2>Webpage Editor</h2>

      {/* Controls for Dragging & Resizing */}
      <div style={{ display: "flex", gap: "10px", marginBottom: "10px" }}>
        <button onClick={() => editor.runCommand("core:component-exit")}>Exit Selection</button>
        <button onClick={() => editor.runCommand("tlb-move")}>Enable Move Mode</button>
        <button onClick={() => editor.runCommand("tlb-resize")}>Enable Resize Mode</button>
      </div>

      {/* AI Template Button */}
      <div style={{ marginBottom: "10px" }}>
        <button onClick={() => setUseAITemplate(!useAITemplate)}>
          {useAITemplate ? "Hide AI Template Options" : "Use AI-Generated Template"}
        </button>
      </div>

      {useAITemplate && (
        <div>
          <textarea
            value={additionalPrompt}
            onChange={(e) => setAdditionalPrompt(e.target.value)}
            placeholder="Enter additional modifications..."
            style={{ width: "100%", height: "60px", padding: "10px" }}
          />
          <button>Generate AI Template</button>
        </div>
      )}

      {/* AI Image Generation Button */}
      <div style={{ marginBottom: "10px" }}>
        <button onClick={() => setUseAIImage(!useAIImage)}>
          {useAIImage ? "Hide AI Image Options" : "Generate AI Image"}
        </button>
      </div>

      {useAIImage && (
        <div style={{ marginBottom: "10px" }}>
          <input
            type="text"
            value={imagePrompt}
            onChange={(e) => setImagePrompt(e.target.value)}
            placeholder="Enter image prompt..."
            style={{ width: "100%", padding: "10px" }}
          />
          <button onClick={generateImage} style={{ padding: "10px", width: "100%", marginTop: "10px" }}>
            Generate Image
          </button>
        </div>
      )}

      {/* Show Generated Image */}
      {generatedImage && (
        <div>
          <h4>Generated Image:</h4>
          <img src={generatedImage} alt="AI Generated" style={{ maxWidth: "100%", marginBottom: "10px" }} />
          <button onClick={addImageToEditor} style={{ padding: "10px", width: "100%" }}>
            Add Image to Editor
          </button>
        </div>
      )}

      {/* GrapesJS Editor */}
      <div id="gjs"></div>

      {/* Navigation Buttons */}
      <div style={{ display: "flex", justifyContent: "space-between", marginTop: "20px" }}>
        <button onClick={() => navigate("/")}>Previous</button>
        <button onClick={goToWebpageView}>Next</button>
      </div>
    </div>
  );
}

export default WebpageEditor;
