import { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { saveAs } from "file-saver";

function WebpageView() {
  const location = useLocation();
  const navigate = useNavigate();
  const userId = new URLSearchParams(location.search).get("user_id");
  const [htmlContent, setHtmlContent] = useState("");
  const [cssContent, setCssContent] = useState("");

  useEffect(() => {
    const savedHtml = localStorage.getItem("savedHtml");
    const savedCss = localStorage.getItem("savedCss");

    if (savedHtml) {
      setHtmlContent(savedHtml);
    }
    if (savedCss) {
      setCssContent(savedCss);
    }
  }, []);

  // Function to download the webpage as a file
  const downloadWebpage = () => {
    const fullHtml = `
      <!DOCTYPE html>
      <html lang="en">
      <head>
        <meta charset="UTF-8">
        <meta name="viewport" content="width=device-width, initial-scale=1.0">
        <title>Generated Webpage</title>
        <style>${cssContent}</style>
      </head>
      <body>${htmlContent}</body>
      </html>
    `;

    const blob = new Blob([fullHtml], { type: "text/html;charset=utf-8" });
    saveAs(blob, "generated_webpage.html");
  };

  return (
    <div>
      <h2>Webpage Preview</h2>

      <div
        style={{
          border: "1px solid #ccc",
          padding: "10px",
          marginBottom: "20px",
          minHeight: "400px",
        }}
        dangerouslySetInnerHTML={{ __html: htmlContent }}
      />

      {/* Buttons for Navigation & Download */}
      <div style={{ display: "flex", justifyContent: "space-between" }}>
        <button onClick={() => navigate(`/editor?user_id=${userId}`)}>Previous</button>
        <button onClick={downloadWebpage}>Download Webpage</button>
      </div>
    </div>
  );
}

export default WebpageView;
